def count_vowels_and_consonants(sentence):
    vowels = "aeiouAEIOU"
    vowel_count = 0
    consonant_count = 0
    for char in sentence:
        if char.isalpha():
            if char in vowels:
                vowel_count += 1
            else:
                consonant_count += 1
    return vowel_count, consonant_count

sentence = "Lambar tana karanta a cikin ƙimar da ta dace da layin umarni kuma tana ƙara 1000 a gare ta."
vowels, consonants = count_vowels_and_consonants(sentence)
print(f"Kuma: {vowels}, Ƙamar: {consonants}")

