import csv

def process_motorcycle_data(file_path):
    weather_data = {}
    motorcycle_count = 0
    try:
        with open(file_path, 'r') as file:
            reader = csv.DictReader(file)
            for row in reader:
                motorcycle_count += 1
                weather = row.get('weather', '').strip().lower()
                if weather not in weather_data:
                    weather_data[weather] = {'count': 0, 'total_temp': 0.0}
                weather_data[weather]['count'] += 1
                temp = row.get('temperature', '0')
                if temp.isdigit():
                    weather_data[weather]['total_temp'] += float(temp)
        for weather, data in weather_data.items():
            avg_temp = data['total_temp'] / data['count'] if data['count'] > 0 else 0
            print(f"Weather: {weather.capitalize()}, Motorcycles: {data['count']}, Avg Temp: {avg_temp:.1f}")
    except FileNotFoundError:
        print("Error: File not found.")
    except Exception as e:
        print(f"An error occurred: {e}")

process_motorcycle_data('motorcycles.csv')
